# util functions
import itertools


def to_lower(group):
    return set(w.lower() for w in group)

def remove_dups(group1, *groups):
    """ removes words from group1 that are also in any of groups. groups are lowercase already """
    # lower2 = to_lower(group2)
    clean1 = set(w for w in set(group1) if w not in set(itertools.chain(*groups)))
    return clean1

# def append_tag(group, tag):
#     return set('"' + w + '", "' + tag.lower() + '"' for w in group)

# def append_empty_tag(group):
#     return set('"' + w + '",' for w in group)

def append_tag(group, tag):
    return ((w,tag.lower()) for w in group)

def append_empty_tag(group):
    return ((w,None) for w in group)

# def merge_sets(*groups):
#     u = set()
#     for g in groups:
#         u = u.union(set(g))
#     return u

def merge_lower(*groups):
    u = set()
    for g in groups:
        u = u.union(to_lower(g))
    return u

def merge_sets(*groups):
    return itertools.chain.from_iterable(groups)



def aux(g):
    return sorted(list(set(e.lower() for e in g)))

